package Exercises;

public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<=3; i++) {
			//System.out.println(i);
			
			for(int y=1; y<=3;y++) {
			
				if(y==2) {
					
					continue;		
			}
				System.out.println(i+"     "+y);
				
			}
				
			}
			
			
		}
	
}
	


